 import axios from 'axios';
 import { useState ,useEffect } from 'react';
 import './App.css';

function App() {

  const [post,setPost] = useState({title:'',body:'', date:'2/2/2020'})


  const [title,setTitle]= useState('')
  const [body,setBody]= useState('')

  const [posts,setPosts]= useState([])



  useEffect(()=>{

      axios.get('http://localhost:3001/posts')
     .then((res)=>{
      console.log(res.data)
      setPosts(res.data)

    })
  },[])


  const handleClick=()=>{

  setPost({title:title , body:body , date:'1/2/2020'})

    axios.post('http://localhost:3001/posts',post)

    .then((res)=>{
      console.log(res.data)
      setPosts(res.data)

    })

  }
  return (
     <>
  <div className='App'>
  <header className='App-header'>

    {posts.map((post)=>{

        return <li> title: {post.title} Body: {post.body} Date:{post.date} </li> 
        })}

<form>

  <input 
  //  type='text'
   placeholder='title' 
   value={title}
   onChange ={ e=> setTitle(e.target.value)}></input><br/>

  <input 
  // type='text' 
  placeholder='body'
  value= {body}
  onChange ={ e=> setBody(e.target.value)}></input><br/>


</form>
<br/>
    <button onClick={handleClick}>POST</button>

  </header>


   </div>
</>
  );
}

export default App;
