 import axios from 'axios';
 import { useState ,useEffect } from 'react';
 import './App.css';

function App() {

  const [post,setPost] = useState({title:'',body:'', date:'2/2/2020'})


  const [addtitle,setTitle]= useState('')
  const [addbody,setBody]= useState('')

  const [posts,setPosts]= useState([])



  useEffect(()=>{

      axios.get('http://localhost:3001/posts')
     .then((res)=>{
      console.log(res.data)
      setPosts(res.data)

    })
  },[posts])


 const handelBtn= ()=>{
  setPost({title:addtitle , body:addbody , date:'1/2/2020'})

 }

  const handleClick=()=>{

    axios.post('http://localhost:3001/posts',{title:addtitle ,body:addbody,date})

    .then((res)=>{
      console.log(res.data)
      setPosts([res.data])

      setTitle('')
      setBody('')
    })

  }
  return (
     <>
  <div className='App'>
  <header className='App-header'>

    {posts.map((post)=>{

        return <li>
     title: {post.title} Body: {post.body} Date:{post.date} 

         </li> 

       })}

  <form>

  <input 
  //  type='text'
   placeholder='title' 
   value={addtitle}
   onChange ={ e=> setTitle(e.target.value)}></input><br/>

  <input 
  // type='text' 
  placeholder='body'
  value= {addbody}
  onChange ={ e=> setBody(e.target.value)}></input><br/>

  </form>
<br/>

   {/* <button onClick={handelBtn}>click me </button> */}

    <button onClick={handleClick}>POST</button><br/>
    <button>Delete</button><br/>
    <button>PUT</button>


  </header>


   </div>
</>
  );
}

export default App;
